const Mutation = {
    /**
     * Create a upvote for post
     *
     * @param {string} userId
     * @param {string} postId
     */
    createupVote: async (root, { input: { userId, postId } }, { UpVote, Vote,Post, User }) => {
      const upVote = await new UpVote({ user: userId, post: postId }).save();
  
      // Add one to votes counting
      await Vote.findOneAndUpdate({ $inc: {votes: 1} }); 
    
      // Push vote to post collection
      await Post.findOneAndUpdate({ _id: postId }, { $push: { upvotes: upVote.id } });
      // Push vote to user collection
      await User.findOneAndUpdate({ _id: userId }, { $push: { upvotes: upVote.id } });
  
      return upVote;
    },
    /**
     * Deletes a uppost vote
     *
     * @param {string} id
     */
    deleteupVote: async (root, { input: { id } }, { UpVote,DownVote, Vote,User, Post }) => {
      const upVote = await UpVote.findByIdAndRemove(id);  
      const downVote = await new DownVote({user: userId, post: postId}).save();
  
      // remove one from vote counting
      await Vote.findOneAndUpdate({$inc: {votes : -1}});

      // delete upvote from users collection
      await User.findOneAndUpdate({_id: upvote.user}, {$pull: { upvotes: upVote.id }});

      // delete upvote from posts collection
      await Post.findByOneAndUpdate({_id: upvote.post}, {$pull: { upvotes: upVote.id }});

      // add down  vote from users collection
      await User.findOneAndUpdate({ _id: downvote.user }, { $push: { downvotes: downVote.id } });
      // add down vote from posts collection
      await Post.findOneAndUpdate({ _id: downvote.post }, { $push: { downvotes: downVote.id } });
  
      return upVote,downVote;
    },
  };
  
  export default { Mutation };

